contracts/Escrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Escrow is Ownable, ReentrancyGuard {
    enum EscrowStatus { Active, Completed, Disputed, Cancelled }

    struct EscrowAgreement {
        uint256 id;
        address payer;
        address payee;
        address tokenAddress;
        uint256 amount;
        uint256 releasedAmount;
        EscrowStatus status;
        uint256 createdAt;
        uint256 deadline;
        string description;
    }

    mapping(uint256 => EscrowAgreement) public escrows;
    mapping(address => uint256[]) public userEscrows;

    uint256 public nextEscrowId = 1;
    uint256 public disputeTimeWindow = 7 days;

    event EscrowCreated(uint256 indexed escrowId, address indexed payer, address indexed payee, uint256 amount);
    event FundsReleased(uint256 indexed escrowId, uint256 amount);
    event EscrowCompleted(uint256 indexed escrowId);
    event DisputeRaised(uint256 indexed escrowId, address indexed raiser);
    event EscrowCancelled(uint256 indexed escrowId);

    constructor() {}

    modifier onlyEscrowParties(uint256 escrowId) {
        require(
            msg.sender == escrows[escrowId].payer || 
            msg.sender == escrows[escrowId].payee,
            "Not authorized"
        );
        _;
    }

    function createEscrow(
        address payee,
        address tokenAddress,
        uint256 amount,
        uint256 deadline,
        string memory description
    ) external payable returns (uint256 escrowId) {
        require(payee != address(0), "Invalid payee address");
        require(amount > 0, "Amount must be greater than 0");
        require(deadline > block.timestamp, "Deadline must be in the future");

        escrowId = nextEscrowId++;

        if (tokenAddress == address(0)) {
            require(msg.value == amount, "Incorrect ETH amount");
        } else {
            IERC20 token = IERC20(tokenAddress);
            require(token.transferFrom(msg.sender, address(this), amount), "Token transfer failed");
        }

        escrows[escrowId] = EscrowAgreement({
            id: escrowId,
            payer: msg.sender,
            payee: payee,
            tokenAddress: tokenAddress,
            amount: amount,
            releasedAmount: 0,
            status: EscrowStatus.Active,
            createdAt: block.timestamp,
            deadline: deadline,
            description: description
        });

        userEscrows[msg.sender].push(escrowId);
        userEscrows[payee].push(escrowId);

        emit EscrowCreated(escrowId, msg.sender, payee, amount);

        return escrowId;
    }

    function releaseFunds(uint256 escrowId, uint256 amount) external onlyEscrowParties(escrowId) nonReentrant {
        EscrowAgreement storage escrow = escrows[escrowId];
        require(escrow.status == EscrowStatus.Active, "Escrow not active");
        require(amount > 0, "Amount must be greater than 0");
        require(escrow.releasedAmount + amount <= escrow.amount, "Insufficient funds in escrow");

        escrow.releasedAmount += amount;

        if (escrow.tokenAddress == address(0)) {
            payable(escrow.payee).transfer(amount);
        } else {
            IERC20 token = IERC20(escrow.tokenAddress);
            require(token.transfer(escrow.payee, amount), "Token transfer failed");
        }

        emit FundsReleased(escrowId, amount);

        if (escrow.releasedAmount == escrow.amount) {
            escrow.status = EscrowStatus.Completed;
            emit EscrowCompleted(escrowId);
        }
    }

    function raiseDispute(uint256 escrowId) external onlyEscrowParties(escrowId) {
        EscrowAgreement storage escrow = escrows[escrowId];
        require(escrow.status == EscrowStatus.Active, "Escrow not active");

        escrow.status = EscrowStatus.Disputed;
        emit DisputeRaised(escrowId, msg.sender);
    }

    function resolveDispute(uint256 escrowId, uint256 payerAmount, uint256 payeeAmount) external onlyOwner {
        EscrowAgreement storage escrow = escrows[escrowId];
        require(escrow.status == EscrowStatus.Disputed, "Escrow not disputed");
        require(payerAmount + payeeAmount == escrow.amount - escrow.releasedAmount, "Amounts don't match remaining funds");

        if (payeeAmount > 0) {
            if (escrow.tokenAddress == address(0)) {
                payable(escrow.payee).transfer(payeeAmount);
            } else {
                IERC20 token = IERC20(escrow.tokenAddress);
                require(token.transfer(escrow.payee, payeeAmount), "Token transfer to payee failed");
            }
        }

        if (payerAmount > 0) {
            if (escrow.tokenAddress == address(0)) {
                payable(escrow.payer).transfer(payerAmount);
            } else {
                IERC20 token = IERC20(escrow.tokenAddress);
                require(token.transfer(escrow.payer, payerAmount), "Token transfer to payer failed");
            }
        }

        escrow.status = EscrowStatus.Completed;
        escrow.releasedAmount = escrow.amount;

        emit EscrowCompleted(escrowId);
    }

    function cancelEscrow(uint256 escrowId) external {
        EscrowAgreement storage escrow = escrows[escrowId];
        require(msg.sender == escrow.payer, "Only payer can cancel");
        require(escrow.status == EscrowStatus.Active, "Escrow not active");
        require(escrow.releasedAmount == 0, "Cannot cancel after funds released");

        escrow.status = EscrowStatus.Cancelled;

        uint256 refundAmount = escrow.amount - escrow.releasedAmount;

        if (escrow.tokenAddress == address(0)) {
            payable(escrow.payer).transfer(refundAmount);
        } else {
            IERC20 token = IERC20(escrow.tokenAddress);
            require(token.transfer(escrow.payer, refundAmount), "Token refund failed");
        }

        emit EscrowCancelled(escrowId);
    }

    function getEscrow(uint256 escrowId) external view returns (EscrowAgreement memory) {
        return escrows[escrowId];
    }

    function getUserEscrows(address user) external view returns (uint256[] memory) {
        return userEscrows[user];
    }

    function setDisputeTimeWindow(uint256 newTimeWindow) external onlyOwner {
        disputeTimeWindow = newTimeWindow;
    }
}

contracts/InvoiceManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IInvoiceNFT {
    function mintReceipt(
        uint256 _invoiceId,
        address _issuer,
        address _client,
        uint256 _amount,
        string memory _description
    ) external returns (uint256);
}

contract InvoiceManager is Ownable, ReentrancyGuard {
    enum InvoiceStatus { Created, Paid, Cancelled, Disputed }

    struct Invoice {
        uint256 id;
        address issuer;
        address client;
        address tokenAddress; // 0x0 for native token
        uint256 amount;
        uint256 amountPaid;
        uint256 dueDate;
        string description;
        InvoiceStatus status;
        bool useEscrow;
        uint256 createdAt;
    }

    struct EscrowInvoice {
        uint256 invoiceId;
        bool isActive;
        uint256 totalAmount;
        uint256 releasedAmount;
    }

    struct Milestone {
        uint256 amount;
        string description;
        bool isCompleted;
        bool isReleased;
    }

    mapping(uint256 => Invoice) public invoices;
    mapping(uint256 => EscrowInvoice) public escrowInvoices;
    mapping(uint256 => Milestone[]) public invoiceMilestones;
    mapping(address => uint256[]) public userInvoices;
    mapping(address => uint256[]) public clientInvoices;

    uint256 public nextInvoiceId = 1;
    uint256 public platformFeePercentage = 250; // 2.5%
    address public feeRecipient;
    IInvoiceNFT public invoiceNFT;

    event InvoiceCreated(uint256 indexed invoiceId, address indexed issuer, address indexed client, uint256 amount, uint256 dueDate, string description);
    event InvoicePaid(uint256 indexed invoiceId, address indexed payer, uint256 amount);
    event ReceiptMinted(uint256 indexed invoiceId, uint256 indexed tokenId, address indexed client);
    event InvoiceCancelled(uint256 indexed invoiceId);
    event EscrowActivated(uint256 indexed invoiceId, uint256 amount);
    event MilestoneCompleted(uint256 indexed invoiceId, uint256 milestoneIndex);
    event MilestoneReleased(uint256 indexed invoiceId, uint256 milestoneIndex, uint256 amount);
    event DisputeRaised(uint256 indexed invoiceId, address indexed raiser);

    constructor() {
        feeRecipient = msg.sender;
    }

    modifier onlyInvoiceParties(uint256 invoiceId) {
        require(
            msg.sender == invoices[invoiceId].issuer || 
            msg.sender == invoices[invoiceId].client,
            "Not authorized"
        );
        _;
    }

    function createInvoice(
        address client,
        address tokenAddress,
        uint256 amount,
        uint256 dueDate,
        string memory description,
        bool useEscrow
    ) external returns (uint256 invoiceId) {
        require(client != address(0), "Invalid client address");
        require(amount > 0, "Amount must be greater than 0");
        require(dueDate > block.timestamp, "Due date must be in the future");

        invoiceId = nextInvoiceId++;

        invoices[invoiceId] = Invoice({
            id: invoiceId,
            issuer: msg.sender,
            client: client,
            tokenAddress: tokenAddress,
            amount: amount,
            amountPaid: 0,
            dueDate: dueDate,
            description: description,
            status: InvoiceStatus.Created,
            useEscrow: useEscrow,
            createdAt: block.timestamp
        });

        userInvoices[msg.sender].push(invoiceId);
        clientInvoices[client].push(invoiceId);

        if (useEscrow) {
            escrowInvoices[invoiceId] = EscrowInvoice({
                invoiceId: invoiceId,
                isActive: true,
                totalAmount: amount,
                releasedAmount: 0
            });

            emit EscrowActivated(invoiceId, amount);
        }

        emit InvoiceCreated(invoiceId, msg.sender, client, amount, dueDate, description);

        return invoiceId;
    }

    function payInvoice(uint256 invoiceId) external payable nonReentrant {
        Invoice storage invoice = invoices[invoiceId];
        require(invoice.id != 0, "Invoice does not exist");
        require(invoice.status == InvoiceStatus.Created, "Invoice not payable");
        require(msg.sender == invoice.client, "Only client can pay");

        uint256 amountToPay = invoice.amount - invoice.amountPaid;

        if (invoice.tokenAddress == address(0)) {
            require(msg.value >= amountToPay, "Insufficient payment");

            if (invoice.useEscrow) {
                // Funds stay in contract for escrow
            } else {
                // Direct payment to issuer
                uint256 fee = (amountToPay * platformFeePercentage) / 10000;
                uint256 issuerAmount = amountToPay - fee;

                payable(invoice.issuer).transfer(issuerAmount);
                payable(feeRecipient).transfer(fee);
            }

            // Refund excess
            if (msg.value > amountToPay) {
                payable(msg.sender).transfer(msg.value - amountToPay);
            }
        } else {
            IERC20 token = IERC20(invoice.tokenAddress);
            require(token.transferFrom(msg.sender, address(this), amountToPay), "Token transfer failed");

            if (!invoice.useEscrow) {
                uint256 fee = (amountToPay * platformFeePercentage) / 10000;
                uint256 issuerAmount = amountToPay - fee;

                require(token.transfer(invoice.issuer, issuerAmount), "Transfer to issuer failed");
                require(token.transfer(feeRecipient, fee), "Fee transfer failed");
            }
        }

        invoice.amountPaid += amountToPay;
        invoice.status = InvoiceStatus.Paid;

        // Mint NFT receipt if invoice is fully paid and NFT contract is set
        if (invoice.amountPaid >= invoice.amount && address(invoiceNFT) != address(0)) {
            try invoiceNFT.mintReceipt(
                invoiceId,
                invoice.issuer,
                invoice.client,
                invoice.amount,
                invoice.description
            ) returns (uint256 tokenId) {
                emit ReceiptMinted(invoiceId, tokenId, invoice.client);
            } catch {
                // NFT minting failed, but payment still succeeded
                // This ensures payment isn't reverted due to NFT issues
            }
        }

        emit InvoicePaid(invoiceId, msg.sender, amountToPay);
    }

    function addMilestones(uint256 invoiceId, Milestone[] memory milestones) external {
        require(invoices[invoiceId].issuer == msg.sender, "Only issuer can add milestones");
        require(invoices[invoiceId].useEscrow, "Invoice must use escrow");

        for (uint i = 0; i < milestones.length; i++) {
            invoiceMilestones[invoiceId].push(milestones[i]);
        }
    }

    function completeMilestone(uint256 invoiceId, uint256 milestoneIndex) external {
        require(invoices[invoiceId].issuer == msg.sender, "Only issuer can complete milestones");
        require(milestoneIndex < invoiceMilestones[invoiceId].length, "Invalid milestone index");

        Milestone storage milestone = invoiceMilestones[invoiceId][milestoneIndex];
        require(!milestone.isCompleted, "Milestone already completed");

        milestone.isCompleted = true;
        emit MilestoneCompleted(invoiceId, milestoneIndex);
    }

    function releaseMilestone(uint256 invoiceId, uint256 milestoneIndex) external {
        Invoice storage invoice = invoices[invoiceId];
        require(invoice.client == msg.sender, "Only client can release milestones");
        require(milestoneIndex < invoiceMilestones[invoiceId].length, "Invalid milestone index");

        Milestone storage milestone = invoiceMilestones[invoiceId][milestoneIndex];
        require(milestone.isCompleted, "Milestone not completed");
        require(!milestone.isReleased, "Milestone already released");

        milestone.isReleased = true;

        EscrowInvoice storage escrow = escrowInvoices[invoiceId];
        escrow.releasedAmount += milestone.amount;

        uint256 fee = (milestone.amount * platformFeePercentage) / 10000;
        uint256 issuerAmount = milestone.amount - fee;

        if (invoice.tokenAddress == address(0)) {
            payable(invoice.issuer).transfer(issuerAmount);
            payable(feeRecipient).transfer(fee);
        } else {
            IERC20 token = IERC20(invoice.tokenAddress);
            require(token.transfer(invoice.issuer, issuerAmount), "Transfer to issuer failed");
            require(token.transfer(feeRecipient, fee), "Fee transfer failed");
        }

        emit MilestoneReleased(invoiceId, milestoneIndex, milestone.amount);
    }

    function cancelInvoice(uint256 invoiceId) external {
        Invoice storage invoice = invoices[invoiceId];
        require(invoice.issuer == msg.sender, "Only issuer can cancel");
        require(invoice.status == InvoiceStatus.Created, "Cannot cancel paid invoice");

        invoice.status = InvoiceStatus.Cancelled;
        emit InvoiceCancelled(invoiceId);
    }

    function raiseDispute(uint256 invoiceId) external onlyInvoiceParties(invoiceId) {
        invoices[invoiceId].status = InvoiceStatus.Disputed;
        emit DisputeRaised(invoiceId, msg.sender);
    }

    function getInvoice(uint256 invoiceId) external view returns (Invoice memory) {
        return invoices[invoiceId];
    }

    function getUserInvoices(address user) external view returns (uint256[] memory) {
        return userInvoices[user];
    }

    function getClientInvoices(address client) external view returns (uint256[] memory) {
        return clientInvoices[client];
    }

    function getInvoiceMilestones(uint256 invoiceId) external view returns (Milestone[] memory) {
        return invoiceMilestones[invoiceId];
    }

    function setPlatformFee(uint256 newFeePercentage) external onlyOwner {
        require(newFeePercentage <= 1000, "Fee cannot exceed 10%");
        platformFeePercentage = newFeePercentage;
    }

    function setFeeRecipient(address newFeeRecipient) external onlyOwner {
        require(newFeeRecipient != address(0), "Invalid fee recipient");
        feeRecipient = newFeeRecipient;
    }

    function setInvoiceNFT(address nftContractAddress) external onlyOwner {
        require(nftContractAddress != address(0), "Invalid NFT contract address");
        invoiceNFT = IInvoiceNFT(nftContractAddress);
    }

    function emergencyWithdraw(address tokenAddress) external onlyOwner {
        if (tokenAddress == address(0)) {
            payable(owner()).transfer(address(this).balance);
        } else {
            IERC20 token = IERC20(tokenAddress);
            token.transfer(owner(), token.balanceOf(address(this)));
        }
    }
}

contracts/InvoiceNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/Base64.sol";

/**
 * @title InvoiceNFT
 * @dev Soulbound NFT receipts for completed invoices
 * These NFTs are non-transferable and serve as permanent proof of payment
 */
contract InvoiceNFT is ERC721, ERC721URIStorage, Ownable {
    using Counters for Counters.Counter;
    using Strings for uint256;

    Counters.Counter private _tokenIdCounter;

    // Mapping from invoice ID to NFT token ID
    mapping(uint256 => uint256) public invoiceToTokenId;

    // Mapping from token ID to invoice data
    mapping(uint256 => InvoiceReceipt) public tokenIdToReceipt;

    // Mapping to track user's NFT receipts
    mapping(address => uint256[]) public userReceipts;

    struct InvoiceReceipt {
        uint256 invoiceId;
        address issuer;
        address client;
        uint256 amount;
        uint256 paidDate;
        string description;
        bool exists;
    }

    event ReceiptMinted(
        uint256 indexed tokenId,
        uint256 indexed invoiceId,
        address indexed issuer,
        address client,
        uint256 amount
    );

    constructor() ERC721("Freya Invoice Receipt", "FIR") {}

    /**
     * @dev Mint a soulbound NFT receipt for a completed invoice
     * Only the contract owner (InvoiceManager) can mint
     */
    function mintReceipt(
        uint256 _invoiceId,
        address _issuer,
        address _client,
        uint256 _amount,
        string memory _description
    ) external onlyOwner returns (uint256) {
        require(invoiceToTokenId[_invoiceId] == 0, "Receipt already exists for this invoice");

        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();

        // Store receipt data
        tokenIdToReceipt[tokenId] = InvoiceReceipt({
            invoiceId: _invoiceId,
            issuer: _issuer,
            client: _client,
            amount: _amount,
            paidDate: block.timestamp,
            description: _description,
            exists: true
        });

        // Map invoice to token
        invoiceToTokenId[_invoiceId] = tokenId;

        // Add to user receipts (both issuer and client get the same NFT)
        userReceipts[_issuer].push(tokenId);
        userReceipts[_client].push(tokenId);

        // Mint to client (primary recipient)
        _safeMint(_client, tokenId);

        // Set token URI
        _setTokenURI(tokenId, generateTokenURI(tokenId));

        emit ReceiptMinted(tokenId, _invoiceId, _issuer, _client, _amount);

        return tokenId;
    }

    /**
     * @dev Generate dynamic metadata for the NFT receipt
     */
    function generateTokenURI(uint256 _tokenId) internal view returns (string memory) {
        require(_exists(_tokenId), "Token does not exist");

        InvoiceReceipt memory receipt = tokenIdToReceipt[_tokenId];

        // Create JSON metadata
        string memory json = Base64.encode(
            bytes(
                string(
                    abi.encodePacked(
                        '{"name": "Freya Invoice Receipt #',
                        receipt.invoiceId.toString(),
                        '",',
                        '"description": "Soulbound receipt for completed invoice payment on Freya platform",',
                        '"image": "',
                        generateSVG(_tokenId),
                        '",',
                        '"attributes": [',
                        '{"trait_type": "Invoice ID", "value": "',
                        receipt.invoiceId.toString(),
                        '"},',
                        '{"trait_type": "Amount", "value": "',
                        (receipt.amount / 1e18).toString(),
                        ' S"},',
                        '{"trait_type": "Paid Date", "value": "',
                        receipt.paidDate.toString(),
                        '"},',
                        '{"trait_type": "Issuer", "value": "',
                        toAsciiString(receipt.issuer),
                        '"},',
                        '{"trait_type": "Client", "value": "',
                        toAsciiString(receipt.client),
                        '"},',
                        '{"trait_type": "Soulbound", "value": "true"}',
                        ']}'
                    )
                )
            )
        );

        return string(abi.encodePacked("data:application/json;base64,", json));
    }

    /**
     * @dev Generate SVG image for the NFT receipt
     */
    function generateSVG(uint256 _tokenId) internal view returns (string memory) {
        InvoiceReceipt memory receipt = tokenIdToReceipt[_tokenId];

        string memory svg = string(
            abi.encodePacked(
                '<svg width="400" height="600" xmlns="http://www.w3.org/2000/svg">',
                '<defs>',
                '<linearGradient id="bg" x1="0%" y1="0%" x2="100%" y2="100%">',
                '<stop offset="0%" style="stop-color:#1e293b;stop-opacity:1" />',
                '<stop offset="100%" style="stop-color:#3730a3;stop-opacity:1" />',
                '</linearGradient>',
                '</defs>',
                '<rect width="400" height="600" fill="url(#bg)" rx="20"/>',
                '<text x="200" y="60" font-family="Arial, sans-serif" font-size="24" font-weight="bold" text-anchor="middle" fill="#ffffff">FREYA</text>',
                '<text x="200" y="90" font-family="Arial, sans-serif" font-size="14" text-anchor="middle" fill="#94a3b8">Invoice Receipt</text>',
                '<rect x="40" y="120" width="320" height="2" fill="#475569"/>',
                '<text x="60" y="160" font-family="Arial, sans-serif" font-size="16" font-weight="bold" fill="#ffffff">Invoice #',
                receipt.invoiceId.toString(),
                '</text>',
                '<text x="60" y="200" font-family="Arial, sans-serif" font-size="14" fill="#94a3b8">Amount Paid</text>',
                '<text x="60" y="220" font-family="Arial, sans-serif" font-size="20" font-weight="bold" fill="#10b981">',
                (receipt.amount / 1e18).toString(),
                ' S</text>',
                '<text x="60" y="270" font-family="Arial, sans-serif" font-size="14" fill="#94a3b8">From</text>',
                '<text x="60" y="290" font-family="Arial, sans-serif" font-size="12" fill="#ffffff">',
                substring(toAsciiString(receipt.issuer), 0, 20),
                '</text>',
                '<text x="60" y="330" font-family="Arial, sans-serif" font-size="14" fill="#94a3b8">To</text>',
                '<text x="60" y="350" font-family="Arial, sans-serif" font-size="12" fill="#ffffff">',
                substring(toAsciiString(receipt.client), 0, 20),
                '</text>',
                '<text x="60" y="390" font-family="Arial, sans-serif" font-size="14" fill="#94a3b8">Description</text>',
                '<text x="60" y="410" font-family="Arial, sans-serif" font-size="12" fill="#ffffff">',
                substring(receipt.description, 0, 30),
                '</text>',
                '<rect x="40" y="450" width="320" height="2" fill="#475569"/>',
                '<text x="200" y="490" font-family="Arial, sans-serif" font-size="12" text-anchor="middle" fill="#94a3b8">Soulbound Receipt</text>',
                '<text x="200" y="510" font-family="Arial, sans-serif" font-size="10" text-anchor="middle" fill="#64748b">Non-transferable proof of payment</text>',
                '<text x="200" y="550" font-family="Arial, sans-serif" font-size="10" text-anchor="middle" fill="#64748b">Sonic Blockchain</text>',
                '</svg>'
            )
        );

        return string(abi.encodePacked("data:image/svg+xml;base64,", Base64.encode(bytes(svg))));
    }

    /**
     * @dev Get all NFT receipts for a user
     */
    function getUserReceipts(address _user) external view returns (uint256[] memory) {
        return userReceipts[_user];
    }

    /**
     * @dev Get receipt data for a token ID
     */
    function getReceiptData(uint256 _tokenId) external view returns (InvoiceReceipt memory) {
        require(_exists(_tokenId), "Token does not exist");
        return tokenIdToReceipt[_tokenId];
    }

    /**
     * @dev Check if a receipt exists for an invoice
     */
    function receiptExists(uint256 _invoiceId) external view returns (bool) {
        return invoiceToTokenId[_invoiceId] != 0;
    }

    /**
     * @dev Override transfer functions to make tokens soulbound (non-transferable)
     */
    function transferFrom(address, address, uint256) public pure override(ERC721, IERC721) {
        revert("InvoiceNFT: Soulbound tokens cannot be transferred");
    }

    function safeTransferFrom(address, address, uint256) public pure override(ERC721, IERC721) {
        revert("InvoiceNFT: Soulbound tokens cannot be transferred");
    }

    function safeTransferFrom(address, address, uint256, bytes memory) public pure override(ERC721, IERC721) {
        revert("InvoiceNFT: Soulbound tokens cannot be transferred");
    }

    function approve(address, uint256) public pure override(ERC721, IERC721) {
        revert("InvoiceNFT: Soulbound tokens cannot be approved");
    }

    function setApprovalForAll(address, bool) public pure override(ERC721, IERC721) {
        revert("InvoiceNFT: Soulbound tokens cannot be approved");
    }

    /**
     * @dev Override tokenURI to use our custom URI generation
     */
    function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
        return super.tokenURI(tokenId);
    }

    /**
     * @dev Override supportsInterface
     */
    function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC721URIStorage) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @dev Override _burn to handle URI storage
     */
    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
    }

    // Utility functions
    function toAsciiString(address x) internal pure returns (string memory) {
        bytes memory s = new bytes(40);
        for (uint i = 0; i < 20; i++) {
            bytes1 b = bytes1(uint8(uint(uint160(x)) / (2**(8*(19 - i)))));
            bytes1 hi = bytes1(uint8(b) / 16);
            bytes1 lo = bytes1(uint8(b) - 16 * uint8(hi));
            s[2*i] = char(hi);
            s[2*i+1] = char(lo);
        }
        return string(abi.encodePacked("0x", string(s)));
    }

    function char(bytes1 b) internal pure returns (bytes1 c) {
        if (uint8(b) < 10) return bytes1(uint8(b) + 0x30);
        else return bytes1(uint8(b) + 0x57);
    }

    function substring(string memory str, uint startIndex, uint length) internal pure returns (string memory) {
        bytes memory strBytes = bytes(str);
        if (startIndex >= strBytes.length) return "";

        uint endIndex = startIndex + length;
        if (endIndex > strBytes.length) {
            endIndex = strBytes.length;
        }

        bytes memory result = new bytes(endIndex - startIndex);
        for (uint i = startIndex; i < endIndex; i++) {
            result[i - startIndex] = strBytes[i];
        }

        return string(result);
    }
}

